import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import Navbar from "../components/Navbar";
import Footer from "../components/Footer";
import { Input } from "../components/ui/input";
import { Button } from "../components/ui/button";
import {
 Select,
 SelectContent,
 SelectItem,
 SelectTrigger,
 SelectValue,
} from "../components/ui/select";
import { Search, Filter } from "lucide-react";

function ProjectCard({ p, onDownload, onCheck }) {
 return (
  <div className="border p-5 rounded-lg bg-white shadow hover:shadow-md transition-all duration-200">
   <h3 className="font-semibold text-lg mb-2">{p.title}</h3>
   <p className="text-sm text-gray-600 mb-2">{p.description}</p>
   <div className="text-xs text-gray-500 mb-2">
    By: {p.uploadedBy || "Unknown"}
   </div>
   <div className="text-xs text-gray-400 mb-2">
    University: {p.university || "N/A"}
   </div>

   <div className="mt-3 flex gap-2">
    <Button
     onClick={() => onDownload(p._id)}
     variant="outline"
     className="text-sm">
     Download
    </Button>
    <Button onClick={() => onCheck(p._id)} className="text-sm bg-blue-600">
     Check Plagiarism
    </Button>
   </div>
  </div>
 );
}

export default function Projects() {
 const [projects, setProjects] = useState([]);
 const [filteredProjects, setFilteredProjects] = useState([]);
 const [searchQuery, setSearchQuery] = useState("");
 const [selectedCategory, setSelectedCategory] = useState("all");
 const nav = useNavigate();

 useEffect(() => {
  fetchProjects();
 }, []);

 useEffect(() => {
  filterProjects();
 }, [searchQuery, selectedCategory, projects]);

 async function fetchProjects() {
  try {
   const token = localStorage.getItem("token");
   const res = await fetch("http://127.0.0.1:5000/api/projects", {
    headers: {
     "Content-Type": "application/json",
     Authorization: `Bearer ${token}`,
    },
   });
   const data = await res.json();
   if (res.ok) {
    setProjects(data.projects || []);
    setFilteredProjects(data.projects || []);
   } else {
    alert(data.msg || "Error fetching projects");
   }
  } catch (err) {
   console.error(err);
   alert("Failed to fetch projects. Check backend connection.");
  }
 }

 function filterProjects() {
  const filtered = projects.filter((p) => {
   const matchesSearch =
    p.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
    p.description.toLowerCase().includes(searchQuery.toLowerCase());
   const matchesCategory =
    selectedCategory === "all" ||
    (p.category && p.category.toLowerCase() === selectedCategory.toLowerCase());
   return matchesSearch && matchesCategory;
  });
  setFilteredProjects(filtered);
 }

 function download(id) {
  window.location = `http://127.0.0.1:5000/api/projects/download/${id}`;
 }

 function check(id) {
  nav(`/plagiarism/${id}`);
 }

 return (
  <div className="min-h-screen flex flex-col bg-gray-50">
   <Navbar />

   <main className="flex-1">
    {/* Header Section */}
    <section className="gradient-hero border-b py-16">
     <div className="container mx-auto px-4">
      <h1 className="text-4xl md:text-5xl font-bold mb-4">
       Discover{" "}
       <span className="bg-gradient-to-r from-blue-500 to-purple-600 bg-clip-text text-transparent">
        Student Projects
       </span>
      </h1>
      <p className="text-lg text-gray-600 max-w-2xl">
       Browse innovative projects from students across universities worldwide.
       Filter by category, search by keywords, and discover groundbreaking work.
      </p>
     </div>
    </section>

    {/* Filters Section */}
    <section className="py-8 border-b bg-gray-100">
     <div className="container mx-auto px-4">
      <div className="flex flex-col md:flex-row gap-4">
       <div className="flex-1 relative">
        <Search className="absolute left-3 top-1/2 -translate-y-1/2 h-4 w-4 text-gray-400" />
        <Input
         placeholder="Search projects..."
         value={searchQuery}
         onChange={(e) => setSearchQuery(e.target.value)}
         className="pl-10"
        />
       </div>

       <Select
        value={selectedCategory}
        onValueChange={(val) => setSelectedCategory(val)}>
        <SelectTrigger className="w-full md:w-[200px]">
         <Filter className="h-4 w-4 mr-2" />
         <SelectValue placeholder="Category" />
        </SelectTrigger>
        <SelectContent>
         <SelectItem value="all">All Categories</SelectItem>
         <SelectItem value="AI">AI</SelectItem>
         <SelectItem value="Blockchain">Blockchain</SelectItem>
         <SelectItem value="Cloud">Cloud</SelectItem>
        </SelectContent>
       </Select>
      </div>

      <div className="mt-4 text-sm text-gray-500">
       Showing {filteredProjects.length} of {projects.length} projects
      </div>
     </div>
    </section>

    {/* Projects Grid Section */}
    <section className="py-12">
     <div className="container mx-auto px-4">
      {filteredProjects.length > 0 ? (
       <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
        {filteredProjects.map((p) => (
         <ProjectCard key={p._id} p={p} onDownload={download} onCheck={check} />
        ))}
       </div>
      ) : (
       <div className="text-center py-12">
        <p className="text-gray-500">
         No projects found matching your criteria.
        </p>
        <Button
         onClick={() => {
          setSearchQuery("");
          setSelectedCategory("all");
         }}
         className="mt-4">
         Clear Filters
        </Button>
       </div>
      )}
     </div>
    </section>
   </main>

   <Footer />
  </div>
 );
}
