/** @format */

import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
export default function PlagiarismReport() {
 const { id } = useParams();
 const [report, setReport] = useState(null);
 useEffect(() => {
  fetchReport();
 }, []);
 async function fetchReport() {
  const res = await fetch("http://127.0.0.1:5000/api/plagiarism/check/" + id);
  const data = await res.json();
  if (res.ok) setReport(data.report);
  else alert(data.msg || "Error");
 }
 if (!report) return <div>Loading...</div>;
 return (
  <div className="bg-white p-4 rounded shadow">
   <h2 className="text-xl">Plagiarism Report</h2>
   <div>Overall similarity: {report.similarityPercentage}%</div>
   <div className="mt-2">
    {report.comparedWith.map((c, idx) => (
     <div key={idx} className="border p-2 my-2">
      <div>Project: {c.projectId}</div>
      <div>Similarity: {c.similarity}%</div>
      <div>
       Snippets:
       <ul>
        {(c.snippets || []).map((s, i) => (
         <li key={i} className="text-sm">
          {s}
         </li>
        ))}
       </ul>
      </div>
     </div>
    ))}
   </div>
  </div>
 );
}
