import React, {useState} from 'react';
export default function UploadProject(){
  const [title,setTitle]=useState(''); const [description,setDescription]=useState(''); const [file,setFile]=useState(null);
  const submit = async (e)=>{
    e.preventDefault();
    const token = localStorage.getItem('token');
    if(!token) return alert('Login first');
    const fd = new FormData();
    fd.append('title', title);
    fd.append('description', description);
    fd.append('file', file);
    const res = await fetch('http://localhost:5000/api/projects/upload',{method:'POST', headers:{'Authorization': 'Bearer '+token}, body: fd});
    const data = await res.json();
    if(res.ok) alert('Uploaded'); else alert(data.msg || 'Error');
  };
  return (
    <div className="max-w-lg mx-auto bg-white p-6 rounded shadow">
      <h2 className="text-xl mb-4">Upload Project</h2>
      <form onSubmit={submit}>
        <input value={title} onChange={e=>setTitle(e.target.value)} placeholder="Title" className="w-full mb-2 p-2 border"/>
        <textarea value={description} onChange={e=>setDescription(e.target.value)} placeholder="Description" className="w-full mb-2 p-2 border"/>
        <input type="file" onChange={e=>setFile(e.target.files[0])} className="mb-2"/>
        <button className="bg-blue-600 text-white px-3 py-1 rounded">Upload</button>
      </form>
    </div>
  );
}
