/** @format */
import React from "react";
import { Link, useNavigate } from "react-router-dom";
import Navbar from "../components/Navbar";
import Footer from "../components/Footer";
import { Button } from "../components/ui/button";
import {
 Card,
 CardContent,
 CardHeader,
 CardTitle,
} from "../components/ui/card";
import { Badge } from "../components/ui/badge";
import {
 Table,
 TableBody,
 TableCell,
 TableHead,
 TableHeader,
 TableRow,
} from "../components/ui/table";
import {
 FolderOpen,
 Eye,
 Download,
 TrendingUp,
 Edit,
 Trash2,
 Plus,
} from "lucide-react";

const Dashboard = () => {
 const navigate = useNavigate();

 const myProjects = [
  {
   id: "1",
   title: "Explainable AI for Healthcare",
   category: "AI",
   status: "Published",
   views: 1250,
   downloads: 45,
   publishDate: "Nov 21, 2024",
  },
  {
   id: "2",
   title: "Smart Contract Auditor",
   category: "Blockchain",
   status: "Draft",
   views: 0,
   downloads: 0,
   publishDate: "-",
  },
 ];

 const stats = [
  {
   icon: FolderOpen,
   label: "Total Projects",
   value: "2",
   color: "text-blue-500",
  },
  { icon: Eye, label: "Total Views", value: "1.3K", color: "text-green-500" },
  { icon: Download, label: "Downloads", value: "45", color: "text-purple-500" },
  {
   icon: TrendingUp,
   label: "Engagement",
   value: "+28%",
   color: "text-orange-500",
  },
 ];

 return (
  <div className="min-h-screen flex flex-col">
   <main className="flex-1 bg-muted/30">
    {/* Header */}
    <section className="gradient-hero border-b py-12">
     <div className="container">
      <h1 className="text-4xl font-bold mb-2">
       Welcome back,{" "}
       <span className="bg-gradient-to-r from-primary to-accent bg-clip-text text-transparent">
        Student
       </span>
      </h1>
      <p className="text-muted-foreground">
       Manage your projects, track performance, and showcase your work.
      </p>

      <Button
       className="mt-4"
       variant="hero"
       onClick={() => navigate("/upload-project")}>
       <Plus className="h-4 w-4 mr-2" /> Upload New Project
      </Button>
     </div>
    </section>

    {/* Stats */}
    <section className="py-8">
     <div className="container grid grid-cols-2 md:grid-cols-4 gap-4">
      {stats.map((stat, index) => (
       <Card key={index} className="gradient-card shadow-soft">
        <CardContent className="pt-6 flex items-center gap-4">
         <div className={`p-3 rounded-lg bg-muted ${stat.color}`}>
          <stat.icon className="h-6 w-6" />
         </div>
         <div>
          <p className="text-2xl font-bold">{stat.value}</p>
          <p className="text-xs text-muted-foreground">{stat.label}</p>
         </div>
        </CardContent>
       </Card>
      ))}
     </div>
    </section>

    {/* Projects Table */}
    <section className="py-8">
     <div className="container">
      <Card className="gradient-card shadow-soft">
       <CardHeader>
        <CardTitle>Your Projects</CardTitle>
       </CardHeader>
       <CardContent>
        <Table>
         <TableHeader>
          <TableRow>
           <TableHead>Title</TableHead>
           <TableHead>Category</TableHead>
           <TableHead>Status</TableHead>
           <TableHead className="text-right">Views</TableHead>
           <TableHead className="text-right">Downloads</TableHead>
           <TableHead className="text-right">Actions</TableHead>
          </TableRow>
         </TableHeader>
         <TableBody>
          {myProjects.map((project) => (
           <TableRow key={project.id}>
            <TableCell className="font-medium">
             <Link
              to={`/project/${project.id}`}
              className="hover:text-primary transition-colors">
              {project.title}
             </Link>
            </TableCell>
            <TableCell>
             <Badge variant="secondary">{project.category}</Badge>
            </TableCell>
            <TableCell>
             <Badge
              variant={project.status === "Published" ? "default" : "outline"}>
              {project.status}
             </Badge>
            </TableCell>
            <TableCell className="text-right">{project.views}</TableCell>
            <TableCell className="text-right">{project.downloads}</TableCell>
            <TableCell className="text-right">
             <div className="flex justify-end gap-2">
              <Button size="icon" variant="ghost">
               <Edit className="h-4 w-4" />
              </Button>
              <Button size="icon" variant="ghost">
               <Trash2 className="h-4 w-4 text-destructive" />
              </Button>
             </div>
            </TableCell>
           </TableRow>
          ))}
         </TableBody>
        </Table>
       </CardContent>
      </Card>
     </div>
    </section>
   </main>

   <Footer />
  </div>
 );
};

export default Dashboard;
