/** @format */
import React, { useState } from "react";
import { toast } from "sonner";
import { Button } from "../components/ui/button";
import {
 Card,
 CardContent,
 CardHeader,
 CardTitle,
} from "../components/ui/card";
import { Input } from "../components/ui/input";
import { Label } from "../components/ui/label";
import { Textarea } from "../components/ui/textarea";
import {
 Select,
 SelectContent,
 SelectItem,
 SelectTrigger,
 SelectValue,
} from "../components/ui/select";
import { Plus } from "lucide-react";

const UploadProject = () => {
 const [projectTitle, setProjectTitle] = useState("");
 const [projectDescription, setProjectDescription] = useState("");
 const [projectCategory, setProjectCategory] = useState("");
 const [projectUniversity, setProjectUniversity] = useState("");
 const [projectMembers, setProjectMembers] = useState("");
 const [projectTechnologies, setProjectTechnologies] = useState("");

 const API_URL = "http://127.0.0.1:5000/api/projects";

 const handleSubmit = async (e) => {
  e.preventDefault();
  const token = localStorage.getItem("token");
  if (!token) return toast.error("You must be logged in to submit a project");

  const body = {
   title: projectTitle,
   description: projectDescription,
   category: projectCategory,
   university: projectUniversity,
   members: projectMembers.split(",").map((m) => m.trim()),
   technologies: projectTechnologies.split(",").map((t) => t.trim()),
  };

  try {
   const res = await fetch(API_URL, {
    method: "POST",
    headers: {
     "Content-Type": "application/json",
     Authorization: `Bearer ${token}`,
    },
    body: JSON.stringify(body),
   });

   const data = await res.json();
   if (res.ok) {
    toast.success("Project submitted successfully!");
    setProjectTitle("");
    setProjectDescription("");
    setProjectCategory("");
    setProjectUniversity("");
    setProjectMembers("");
    setProjectTechnologies("");
   } else {
    toast.error(data.msg || "Project submission failed");
   }
  } catch (err) {
   console.error(err);
   toast.error("Something went wrong!");
  }
 };

 return (
  <div className="min-h-screen flex justify-center items-start p-4">
   <Card className="w-full max-w-2xl gradient-card shadow-soft">
    <CardHeader>
     <CardTitle className="flex items-center gap-2">
      <Plus className="h-5 w-5" />
      Submit New Project
     </CardTitle>
    </CardHeader>
    <CardContent>
     <form onSubmit={handleSubmit} className="space-y-6">
      <div className="space-y-2">
       <Label htmlFor="title">Project Title *</Label>
       <Input
        id="title"
        placeholder="Enter your project title"
        value={projectTitle}
        onChange={(e) => setProjectTitle(e.target.value)}
        required
       />
      </div>

      <div className="space-y-2">
       <Label htmlFor="category">Category *</Label>
       <Select
        value={projectCategory}
        onValueChange={setProjectCategory}
        required>
        <SelectTrigger>
         <SelectValue placeholder="Select category" />
        </SelectTrigger>
        <SelectContent>
         <SelectItem value="AI">Artificial Intelligence</SelectItem>
         <SelectItem value="Blockchain">Blockchain</SelectItem>
         <SelectItem value="Cloud">Cloud Computing</SelectItem>
         <SelectItem value="Web">Web Development</SelectItem>
         <SelectItem value="Mobile">Mobile Development</SelectItem>
         <SelectItem value="Data">Data Science</SelectItem>
        </SelectContent>
       </Select>
      </div>

      <div className="space-y-2">
       <Label htmlFor="description">Description *</Label>
       <Textarea
        id="description"
        placeholder="Describe your project in detail..."
        rows="6"
        value={projectDescription}
        onChange={(e) => setProjectDescription(e.target.value)}
        required
       />
      </div>

      <div className="space-y-2">
       <Label htmlFor="university">University/Institution *</Label>
       <Input
        id="university"
        placeholder="Enter your institution name"
        value={projectUniversity}
        onChange={(e) => setProjectUniversity(e.target.value)}
        required
       />
      </div>

      <div className="space-y-2">
       <Label htmlFor="members">Team Members</Label>
       <Input
        id="members"
        placeholder="Comma-separated names"
        value={projectMembers}
        onChange={(e) => setProjectMembers(e.target.value)}
       />
      </div>

      <div className="space-y-2">
       <Label htmlFor="technologies">Technologies Used</Label>
       <Input
        id="technologies"
        placeholder="e.g., Python, React, TensorFlow"
        value={projectTechnologies}
        onChange={(e) => setProjectTechnologies(e.target.value)}
       />
      </div>

      <Button type="submit" variant="hero" className="w-full">
       Submit Project
      </Button>
     </form>
    </CardContent>
   </Card>
  </div>
 );
};

export default UploadProject;
