import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { Button } from "../components/ui/button";
import {
 Card,
 CardContent,
 CardDescription,
 CardHeader,
 CardTitle,
} from "../components/ui/card";
import { Input } from "../components/ui/input";
import { Label } from "../components/ui/label";
import {
 Tabs,
 TabsContent,
 TabsList,
 TabsTrigger,
} from "../components/ui/tabs";
import { GraduationCap } from "lucide-react";
import { toast } from "sonner";

const Login = () => {
 const navigate = useNavigate();

 // Login state
 const [loginEmail, setLoginEmail] = useState("");
 const [loginPassword, setLoginPassword] = useState("");

 // Register state
 const [registerName, setRegisterName] = useState("");
 const [registerEmail, setRegisterEmail] = useState("");
 const [registerPassword, setRegisterPassword] = useState("");
 const [registerUniversity, setRegisterUniversity] = useState("");

 // ---- Handle Login ----
 const handleLogin = async (e) => {
  e.preventDefault();
  try {
   const res = await fetch("http://127.0.0.1:5000/api/auth/login", {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({ email: loginEmail, password: loginPassword }),
   });
   const data = await res.json();

   if (res.ok) {
    localStorage.setItem("token", data.token);
    localStorage.setItem("user", JSON.stringify(data.user));
    toast.success("Login successful!");
    navigate("/dashboard");
   } else {
    toast.error(data.msg || "Login failed");
   }
  } catch (err) {
   console.error(err);
   toast.error("Something went wrong!");
  }
 };

 // ---- Handle Register ----
 const handleRegister = async (e) => {
  e.preventDefault();
  try {
   const res = await fetch("http://127.0.0.1:5000/api/auth/register", {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({
     name: registerName,
     email: registerEmail,
     password: registerPassword,
     university: registerUniversity,
    }),
   });
   const data = await res.json();

   if (res.ok) {
    toast.success("Registration successful! Please login.");
   } else {
    toast.error(data.msg || "Registration failed");
   }
  } catch (err) {
   console.error(err);
   toast.error("Something went wrong!");
  }
 };

 return (
  <div className="min-h-screen gradient-hero flex items-center justify-center p-4">
   <div className="w-full max-w-md">
    <Link to="/" className="flex items-center justify-center gap-2 mb-8">
     <GraduationCap className="h-8 w-8 text-primary" />
     <span className="text-2xl font-bold bg-gradient-to-r from-primary to-accent bg-clip-text text-transparent">
      ProjectHub
     </span>
    </Link>

    <Card className="gradient-card shadow-hover">
     <CardHeader className="text-center">
      <CardTitle className="text-2xl">Welcome</CardTitle>
      <CardDescription>
       Login or create an account to get started
      </CardDescription>
     </CardHeader>
     <CardContent>
      <Tabs defaultValue="login" className="w-full">
       <TabsList className="grid w-full grid-cols-2 mb-6">
        <TabsTrigger value="login">Login</TabsTrigger>
        <TabsTrigger value="register">Register</TabsTrigger>
       </TabsList>

       {/* ---- LOGIN TAB ---- */}
       <TabsContent value="login">
        <form onSubmit={handleLogin} className="space-y-4">
         <div className="space-y-2">
          <Label htmlFor="login-email">Email</Label>
          <Input
           id="login-email"
           type="email"
           placeholder="your.email@university.edu"
           value={loginEmail}
           onChange={(e) => setLoginEmail(e.target.value)}
           required
          />
         </div>

         <div className="space-y-2">
          <Label htmlFor="login-password">Password</Label>
          <Input
           id="login-password"
           type="password"
           placeholder="••••••••"
           value={loginPassword}
           onChange={(e) => setLoginPassword(e.target.value)}
           required
          />
         </div>

         <Button type="submit" variant="hero" className="w-full">
          Login
         </Button>
        </form>
       </TabsContent>

       {/* ---- REGISTER TAB ---- */}
       <TabsContent value="register">
        <form onSubmit={handleRegister} className="space-y-4">
         <div className="space-y-2">
          <Label htmlFor="register-name">Full Name</Label>
          <Input
           id="register-name"
           placeholder="John Doe"
           value={registerName}
           onChange={(e) => setRegisterName(e.target.value)}
           required
          />
         </div>

         <div className="space-y-2">
          <Label htmlFor="register-email">Email</Label>
          <Input
           id="register-email"
           type="email"
           placeholder="your.email@university.edu"
           value={registerEmail}
           onChange={(e) => setRegisterEmail(e.target.value)}
           required
          />
         </div>

         <div className="space-y-2">
          <Label htmlFor="register-university">University/Institution</Label>
          <Input
           id="register-university"
           placeholder="MIT School of Engineering"
           value={registerUniversity}
           onChange={(e) => setRegisterUniversity(e.target.value)}
           required
          />
         </div>

         <div className="space-y-2">
          <Label htmlFor="register-password">Password</Label>
          <Input
           id="register-password"
           type="password"
           placeholder="••••••••"
           value={registerPassword}
           onChange={(e) => setRegisterPassword(e.target.value)}
           required
          />
         </div>

         <Button type="submit" variant="hero" className="w-full">
          Create Account
         </Button>
        </form>
       </TabsContent>
      </Tabs>
     </CardContent>
    </Card>

    <p className="text-center text-sm text-muted-foreground mt-4">
     <Link to="/" className="hover:text-primary transition-colors">
      ← Back to Home
     </Link>
    </p>
   </div>
  </div>
 );
};

export default Login;
