import React, {useState} from 'react';
import { useNavigate } from 'react-router-dom';
export default function Signup(){
  const [name,setName]=useState(''); const [email,setEmail]=useState(''); const [password,setPassword]=useState(''); const [role,setRole]=useState('student');
  const nav = useNavigate();
  const submit = async (e)=>{
    e.preventDefault();
    const res = await fetch('http://localhost:5000/api/auth/signup',{method:'POST',headers:{'Content-Type':'application/json'},body:JSON.stringify({name,email,password,role})});
    const data = await res.json();
    if(res.ok){ localStorage.setItem('token', data.token); localStorage.setItem('user', JSON.stringify(data.user)); nav('/dashboard'); } else { alert(data.msg || 'Error'); }
  };
  return (
    <div className="max-w-md mx-auto bg-white p-6 rounded shadow">
      <h2 className="text-xl font-semibold mb-4">Signup</h2>
      <form onSubmit={submit}>
        <input value={name} onChange={e=>setName(e.target.value)} placeholder="Name" className="w-full mb-2 p-2 border"/>
        <input value={email} onChange={e=>setEmail(e.target.value)} placeholder="Email" className="w-full mb-2 p-2 border"/>
        <input value={password} onChange={e=>setPassword(e.target.value)} placeholder="Password" type="password" className="w-full mb-2 p-2 border"/>
        <select value={role} onChange={e=>setRole(e.target.value)} className="w-full mb-2 p-2 border">
          <option value="student">Student</option>
          <option value="mentor">Mentor</option>
          <option value="admin">Admin</option>
        </select>
        <button className="bg-green-600 text-white px-3 py-1 rounded">Signup</button>
      </form>
    </div>
  );
}
