/** @format */
import React from "react";
import { Link } from "react-router-dom";
import PersonaCards from "../components/PersonaCards";
import Footer from "../components/Footer"; // ✅ import footer
import { Features } from "../components/Features";
import { TeamInfo } from "../components/TeamInfo"; // ✅ import TeamInfo
import { GraduationCap, MapPin } from "lucide-react";
import { ScopeFeasibility } from "../components/ScopeFeasibility";

export default function About() {
 const user = JSON.parse(localStorage.getItem("user") || "null");

 return (
  <div className="flex flex-col min-h-screen">
   <main className="flex-grow p-6">
    {!user && (
     <div>
      Please{" "}
      <Link to="/login" className="text-blue-600">
       login
      </Link>
     </div>
    )}

    {user && (
     <>
      {/* <div className="mt-4 text-lg">
       Hello, <span className="font-semibold">{user.name}</span> ({user.role})
      </div>

      {user.role === "student" && (
       <div className="mt-4">
        <Link
         to="/upload"
         className="text-white bg-blue-600 px-3 py-1 rounded hover:bg-blue-700">
         Upload Project
        </Link>
       </div>
      )} */}

      <div className="mt-4">
       <Link to="/projects" className="text-blue-600 hover:underline">
        View Projects
       </Link>
      </div>

      {/* Persona Section */}
      <div className="mt-10">
       <PersonaCards />
      </div>

      {/* Features Section */}
      <div className="mt-10">
       <Features />
      </div>
      <div className="mt-10">
       <ScopeFeasibility />
      </div>

      {/* Team Info Section */}
      <div className="mt-10">
       <TeamInfo />
      </div>
     </>
    )}
   </main>

   {/* ✅ Add footer at bottom */}
   <Footer />
  </div>
 );
}
